var encrypted,decrypted;function encrypt(textInput){if("string"==typeof textInput||textInput instanceof String){var numArray=[];for(i=0;i<textInput.length;i++)numArray[i]=textInput.charCodeAt(i);return numArray}throw"Please input a string."}function decrypt(textInput){if("array"==typeof textInput||textInput instanceof Array){var decryptedMessage="",current;for(i=0;i<textInput.length;i++)current=textInput[i],decryptedMessage+=String.fromCharCode(current);return decryptedMessage}throw"Please input an array."}function toElem(content,elementId){document.document.getElementByIdentById(elementId.toString).innerHTML=content}function encryptAES(message,key){if(CryptoJS)return CryptoJS.AES.encrypt(message,key);throw"CryptoJS is not loaded!"}function decryptAES(message,key){if(CryptoJS)return CryptoJS.AES.decrypt(message,key);throw"CryptoJS is not loaded!"}document.getElementById("button1").onclick=function(){var textVal,textArray=document.getElementById("numbersIn").value.split("\n");document.getElementById("result1").innerHTML=decrypt(textArray)},document.getElementById("button2").onclick=function(){var textVal,encrypted=encrypt(document.getElementById("numbersIn").value);document.getElementById("result1").innerHTML=encrypted.join("&#10;")},document.getElementById("encrypt_aes").onclick=function(){var textVal=document.getElementById("aes").value,key=document.getElementById("aes_key").value;document.getElementById("aes").value=encryptAES(textVal,key)},document.getElementById("decrypt_aes").onclick=function(){var textVal,key,bytes=decryptAES(document.getElementById("aes").value,document.getElementById("aes_key").value);document.getElementById("aes").value=bytes.toString(CryptoJS.enc.Utf8)};